// Set state on account type
const TaskReducer = (state = 'null', action) => {
  switch (action.type) {
    case "ACCOUNT":
      if(state.value)
        return {...state, value: true};
      else
        return {...state, value: false};
    default:
      return {...state, value: ""};
  }
};

export default TaskReducer;
