import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Button from 'react-bootstrap/Button';
import { faPlay } from '@fortawesome/free-solid-svg-icons';

class QuizValide extends React.Component {

  constructor(props) {
    super();
    this.state = {
      path: 'classAnimal',
      name: 'Pourse',
    };
  }

  validateQuiz = () => {
    let p = this.props.path.normalize("NFD").replace(/[\u0300-\u036f]/g, "")
    let n = this.props.name.normalize("NFD").replace(/[\u0300-\u036f]/g, "")
    const sound = require(`./../audio/${p}/${n}.mp3`);
    new Audio(sound).play();
  }

  onClick() {
    this.validateQuiz()
  }

  render() {
    return (
      <div>
        <Button className="btn-secondary" onClick={this.onClick.bind(this)}>
          <FontAwesomeIcon icon={faPlay} id={this.state.path + "/" + this.state.name} ></FontAwesomeIcon>
        </Button>
      </div>
    );
  }
}

export default QuizValide;
