import React from "react";

class AudioTest extends React.Component{

    constructor(props) {
        super(props);
        this.props = "";
    }

  playAudio = () => {
    new Audio("" + this.props).play();
  }

  render() {
    return (
        <div>
          <button onClick={this.playAudio}>PLAY AUDIO</button>
        </div>
    );
  }
}

export default AudioTest;
