import { ImageWord } from './ImageWord';


export function ImageQuiz(path) {
    let lienData = require('./../images/' + path + '.json');
        console.log(lienData);
    return (
        <div>
            {
                lienData.map((data, index) => (
                    <div key={index}>
                        <br />
                        {
                            data.imageListe.map((imageName, indexImage) => (
                                <u key={indexImage}>

                                    {imageName == images ? (
                                        null
                                    ) : (
                                        <img id="imageWord" src={images[imageName + '.png']} />
                                    )
                                    }
                                </u>
                            ))
                        }
                        <br />
                        {data.phrase}
                    </div>
                ))
            }
        </div>
    );
}
