import React, {Component} from 'react';
import Nav from 'react-bootstrap/Nav';
import { NavLink } from 'react-router-dom';
import Dropdown from 'react-bootstrap/Dropdown';

export default function NavBar(isAdmin) {

    let titres = require('./../texte/Titres.json');
    const titresTxt = [];
    
    titres.forEach(data =>{
        if(isAdmin.isAdmin == "true"){
            titresTxt.push(
                <Dropdown.Item>
                    <Nav.Link as={NavLink} exact to={"/" + factor(data.Titre)} replace><b className='text-black'>{data.Titre}</b>
                        <Nav.Link as={NavLink} exact to="/" replace><div className='text-danger text-center'><u>edit</u></div></Nav.Link>
                    </Nav.Link>
                </Dropdown.Item>
            )
        }else{
            titresTxt.push(
                <Dropdown.Item>
                    <Nav.Link as={NavLink} exact to={"/" + factor(data.Titre)} replace><b className='text-black'>{data.Titre}</b></Nav.Link>
                </Dropdown.Item>
            )
        }
    });

    return(
        <Nav className="me-auto bg-light border rounded" expand="lg">
            <Nav.Link className="border rounded" as={NavLink} exact to="/" replace><b className='text-black'><u>Connexion</u></b></Nav.Link>
            <Dropdown>
                <Dropdown.Toggle variant="info" id="dropdown-basic">
                    Textes
                </Dropdown.Toggle>

                <Dropdown.Menu>
                    {titresTxt}
                </Dropdown.Menu>
            </Dropdown>
        </Nav>
    )
}


export function factor(t) {
    t = t.normalize("NFD").replace(/[\u0300-\u036f]/g, "");
    while(t.includes(" ")){
        t = t.replace(" ", '');
    }
    return t;
}
