import React from "react";

class AudioTest extends React.Component{

  constructor(props) {
      super();
      this.path = props;
  }

  playAudio = () => {
    
    let str = this.path.path.normalize("NFD").replace(/[\u0300-\u036f]/g, "")
    console.log(str);
    const sound = require(`./../audio/${str}/${this.path.name}.mp3`);
    new Audio(sound).play();
  }

  render() {
    return (
        <div>
          <button onClick={this.playAudio}>PLAY AUDIO</button>
        </div>
    );
  }
}

export default AudioTest;
