import { ImageQuiz } from './ImageQuiz';


const images = getImagePaths(require.context('./../images/', false, /\.(png|jpe?g|svg)$/));

export function ImageWord(path, isQuiz) {
    let lienData = require('./../' + path + '.json');
    const shuffleArray = (arr) => {
        // leave arr as it is (immutable data in react)
        const copy = [...arr];
        // our output array
        const output = [];
        // while there are items
        while (copy.length > 0) {
            // removes 1 random element from copy and adds it to output;
            output.push(copy.splice(Math.floor(Math.random() * copy.length), 1));
        }
        // return our random array
        return output;
    };
    return (
        <div>
            {
                lienData.map((data, index) => (
                    <div key={index} id={index}>
                        
                        <br />
                        {
                            data.imageListe.map((imageName, indexImage) => (
                                <u key={indexImage}>

                                    {imageName == images ? (
                                        null
                                    ) : (
                                        <img id="imageWord" src={images[imageName + '.png']} />
                                    )
                                    }
                                </u>
                            ))
                        }
                        <br />
                        {
                            isQuiz ?
                            (
                                ImageQuiz(data.phrase)
                            ) : (
                                data.phrase
                            )
                        }
                    </div>
                  
                ))
                
            }
            {console.log(lienData)}
            {shuffleArray(lienData)}
        </div>
        
    );
}


function getImagePaths(i) {
    let images = {};
    i.keys().map((item) => { images[item.replace('./', '')] = i(item); });
    return images;
}
