export const addTask = (title) => {
  return {
    type: "ADD_TASK",
    payload: {
      title: title,
    },
  };
};

export const removeTask = (id) => {
  return {
    type: "REMOVE_TASK",
    payload: {
      id: id,
    },
  };
};
