import lienData from './../images/3elien.json';

const images = getImagePaths(require.context('./../images/', false, /\.(png|jpe?g|svg)$/));

export function ImageWord() {
    let data = { username: 'example' };
        fetch('./../images/3elien.json')
            .then((response) => response.json())
            .then((data) => console.log(data));
    console.log(data);
    return (
        <div>
            {
                lienData.map((data, index) => (
                    <div key={index}>
                        <br />
                        {
                            data.imageListe.map((imageName, indexImage) => (
                                <u key={indexImage}>

                                    {imageName == images ? (
                                        null
                                    ) : (
                                        <img id="imageWord" src={images[imageName + '.png']} />
                                    )
                                    }
                                </u>
                            ))
                        }
                        <br />
                        {data.phrase}
                    </div>
                ))
            }
        </div>
    );
}

function getImagePaths(i) {
    let images = {};
    i.keys().map((item) => { images[item.replace('./', '')] = i(item); });
    return images;
}
