import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Button from 'react-bootstrap/Button';
import { faCheck } from '@fortawesome/free-solid-svg-icons';
import { addTask } from "../actions/TasksActions";

import { connect } from "react-redux";
import Tasks from "./Tasks";

class QuizValide extends React.Component {

  constructor(props) {
    super();
    this.state = {
      score: ""
    }
  }

  validateQuiz = (props) => {
    let boxGood = document.getElementsByClassName(true);
    let boxBad = document.getElementsByClassName(false);

    let score = 0;

    for (const element of boxGood) {
      if (element.checked) {
        score++;
      } else {
        score--;
      }
    }
    for (const element of boxBad) {
      if (element.checked) {
        score--;
      }
    }

    return score
  }

  onClick() {
    let score = this.validateQuiz()
    {

    }

    return score
  }

  render(props) {
    let score
    return (
      <div>
        <Button className="btn-secondary" onClick={this.onClick.bind(this)}>
          <FontAwesomeIcon icon={faCheck} id="Quiz" ></FontAwesomeIcon>
        </Button>

        <Tasks />
      </div>
    );
  }
}

/* export default QuizValide */;

export default connect(null, { add: addTask })(QuizValide);
