import React from "react";

import { connect } from "react-redux";

import { removeTask } from "../actions/TasksActions";

const Task = (props) => {
  return (
    <li>
      <span onClick={() => props.remove(props.task.id)}>[Supprimer]</span>{" "}
      {props.task.title}
    </li>
  );
};

export default connect(null, {
  remove: removeTask,
})(Task);
