import React, {Component} from 'react';
import Nav from 'react-bootstrap/Nav';
import { NavLink } from 'react-router-dom';
import { HomeUser } from './HomeUser';
import "react-bootstrap/createUtilityClasses";

export default function NavBar(isAdmin) {

    let titres = require('./../texte/Titres.json');
    
    const titresTxt = [];
    
    titres.forEach(data =>{
        if(isAdmin.isAdmin == "true"){
            titresTxt.push(
                <Nav.Link as={NavLink} exact to="/" replace><b className='text-black'>{data.Titre}</b>
                    <Nav.Link as={NavLink} exact to="/" replace><div className='text-danger text-center'><u>edit</u></div></Nav.Link>
                </Nav.Link>
            )
        }else{
            titresTxt.push(
                <Nav.Link as={NavLink} exact to="/" replace><b className='text-black'>{data.Titre}</b></Nav.Link>
            )
        }
    });


    return(
        <Nav className="me-auto bg-light border rounded" expand="lg">
            <Nav.Link className="border rounded" as={NavLink} exact to="/" replace component={HomeUser}><b className='text-black'><u>Connexion</u></b></Nav.Link>
            {titresTxt}
        </Nav>
    )
}
