import lienData from './../images/3elien.json';

const birthdayArray = [
    { name: 'John', birthday: '25/12/1990', id: '123' },
    { name: 'Abby', birthday: '22/10/1991', id: '124' },
];

const images = loadAllImage(require.context('./../images/3elien', false, /\.(png|jpe?g|svg)$/));


export function ImageWord() {
    return (
        <div>
            {
                lienData.map((data) =>(
                    <div>
                        <br/>
                        {
                        data.imageListe.map((imageName) => (
                            <div>
                                {imageName}
                            </div>
                        ))
                        
                        }
                        <br/>
                        {data.phrase}
                    </div>
                    )
                )
            }
        </div>

    );
}

function loadAllImage(i) {

    let images = {};
    i.keys().map((item, index) => { images[item.replace('./', '')] = i(item); });

    return images;
}

