import lienData from './../images/3elien.json';


const images = loadAllImage(require.context('./../images/3elien', false, /\.(png|jpe?g|svg)$/));
const s = './' + images[0];
export function ImageWord() {
    return (
        <div>
            {
                lienData.map((data, index) => (
                    <div key={data.id}>
                        {console.log(index)}
                        <br />
                        {
                            data.imageListe.map((imageName) => (
                                <div >
                                    
                                    {imageName == images ? (
                                        null
                                    ) : (
                                        <div>
                                            
                                            <img src= {`${process.env.PUBLIC_URL}/assets/images/deux.png`}  alt={imageName}/>
                                        </div>
                                    )
                                    }
                                </div>
                            ))
                        }
                        <br />
                        {data.phrase}
                    </div>
                ))
            }
        </div>
    );
}

function loadAllImage(i) {

    let images = {};
    i.keys().map((item) => { images[item.replace('./', '')] = i(item); });

    return images;
}
