export function ImageQuiz(date, p) {
    let isQuestion = p.endsWith('?');

    if (isQuestion) {
        let question =
            <div id={p}>
                {p}
            </div>
        return (question);
    }
    else {
        let choix =
            <div className="check" id={p}>
                <input type="checkbox" value={p} />{p}
            </div>
        date.reponse.map((rep, indexRep) => (
            <div id={p}>
                {p}
            </div>
        ))
        return (choix);
    }
}
