import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Button from 'react-bootstrap/Button';
import { faCheck } from '@fortawesome/free-solid-svg-icons';

class QuizValide extends React.Component {

  constructor(props) {
    super();
    this.state = {
      path: 'classAnimal',
      name: 'Pourse',
    };
  }

  validateQuiz = () => {
    if (document.getElementsByClassName('true').checked) {
        alert("checked");
    } else {
        alert("You didn't check it! Let me check it for you.");
    }
  }

  onClick() {
    this.validateQuiz()
  }

  render() {
    return (
      <div>
        <Button className="btn-secondary" onClick={this.onClick.bind(this)}>
          <FontAwesomeIcon icon={faCheck} id="Quiz" ></FontAwesomeIcon>
        </Button>
      </div>
    );
  }
}

export default QuizValide;
