import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Button from 'react-bootstrap/Button';
import { faCheck } from '@fortawesome/free-solid-svg-icons';
import Tasks from "./Tasks";
import AddTask from "./AddTask";

class QuizValide extends React.Component {

  constructor(props) {
    super();
    this.state = {
      score: ""
    }
  }

  validateQuiz = () => {
    let boxGood = document.getElementsByClassName(true);
    let boxBad = document.getElementsByClassName(false);

    let score = 0;

    for (const element of boxGood) {
      if (element.checked) {
        score++;
      } else {
        score--;
      }
    }
    for (const element of boxBad) {
      if (element.checked) {
        score--;
      }
    }

  }

  onClick() {
    this.validateQuiz()
  }

  render(props) {
    let score

    return (
      <div>
        <h1>Admin</h1>
        <AddTask add={props.add} />
        <Tasks data={props.data} remove={props.remove} />
      </div>
    );
  }
}

export default QuizValide;
