import { v4 } from "uuid";

const TaskReducer = (task = {}, action) => {
  switch (action.type) {
    case "ADD_TASK":
      return {
        id: v4(),
        title: action.payload.title,
      };
    default:
      return task;
  }
};

export default TaskReducer;
