export function ImageQuiz(date, p) {
    const images = getImagePaths(require.context('./../images/', false, /\.(png|jpe?g|svg)$/));

    let choix =
            <div id={p}>
                {p}
            </div>
        
            date.reponse.map((choix, indexRep) => (
                

                    <div className="check" id={choix}>
                        <input type="checkbox" value={p} />{choix}
                    </div>

                

            ))
        
    return (choix);

}

function getImagePaths(i) {
    let images = {};
    i.keys().map((item) => { images[item.replace('./', '')] = i(item); });
    return images;
}
