import React from "react";

class AudioTest extends React.Component{

  constructor(props) {
      super();
      this.state = {
        path : props.path,
        name : props.name,
      };

  }

  playAudio = () => {
    let str = this.state.path.normalize("NFD").replace(/[\u0300-\u036f]/g, "")
    const sound = require(`./../audio/${str}/${this.path.name}.mp3`);
    new Audio(sound).play();
  }

  render() {
    return (
        <div>
          <button id={this.state.path + "/" + this.state.name} onClick={this.playAudio}>PLAY AUDIO</button>
        </div>
    );
  }
}

export default AudioTest;
