export function ImageQuiz(path) {
    let text = "Pourquoi on veut construire un nouveau pont?";
    console.log(text);
    let isQuestion = text.endsWith("?");
    console.log(isQuestion);
    return (
        <div>
            {
                isQuestion ? 
                (
                    <div>
                        {path}
                    </div>
                ):(
                <div className="topping">
                    <input type="checkbox" id={path} name="topping" value={path} />{path}
                </div>
                )
            }
        </div>
    );
}
