import React from "react";
import Editor from './Editor';

class UserInfo extends React.Component {

  constructor(props) {
    super();
    this.state = {
      name: 'classAnimal',
      password: 'Pourse',
      date: '',
      scoreTexte:[
        
      ],
      message: ""
    };
    
  }

  UpdateScoreTexte(message)
  {
    this.setState({
        message: message
      });
  }

  setScoreTexte = () => {
    let titres = require('./../texte/Titres.json');
    for (const element of titres) {
        this.scoreTexte.push(element);
      }

  }

  render() {
    return (
      <div>
        <h1>
          {this.state.message}
        </h1>
        <hr/>
        <Editor
          UpdateScoreTexte={this.UpdateScoreTexte.bind(this)}
        />
      </div>
    );
  }
}

export default UserInfo;
