import React, { Component } from 'react';
import { Accordion, Button } from 'react-bootstrap';
import NavBar from './NavBar';
import {faCheck, faX} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Dropzone from './DragDrop';



class AdminEdit extends Component{
    constructor(props){
        super(props);
        this.state = {phrases : [], questions : []};
        this.idPhrases = 0;
        this.idQuestions = 0;
        this.idAnswer = 0;
        this.addPhrases();
        this.addQuestions();
    }

    addPhrases(){
        let arr = this.state.phrases;
        arr.push(<div><input className="awap" type="text" id={"p" + this.idPhrases} placeholder="Nouvelle phrase"></input></div>); 
        
        this.setState({phrases : arr});
        
        this.idPhrases++;
    }

    addQuestions(){
        let arr = this.state.questions;
        arr.push(
            <div>
                <Accordion defaultActiveKey="0">
                    <Accordion.Item eventKey="0">
                                <Accordion.Header>Question {this.idQuestions + 1}</Accordion.Header>
                                <Accordion.Body>
                                    <input className="awap" type="text" id={"q" + this.idQuestions} placeholder="Nouvelle question"></input>
                                    <Dropzone />
                                </Accordion.Body>
                                <Accordion.Body>
                                    <FontAwesomeIcon icon={faCheck} className="text-success"></FontAwesomeIcon>
                                    <input className="awap" type="text" id={"a" + this.idAnswer} placeholder="Réponse 1 (bonne réponse)"></input>
                                    <Dropzone />
                                </Accordion.Body>
                                <Accordion.Body>
                                    <FontAwesomeIcon icon={faX} className="text-danger"></FontAwesomeIcon>
                                    <input className="awap" type="text" id={"a" + (this.idAnswer + 1)} placeholder="Réponse 2"></input>
                                    <Dropzone />
                                </Accordion.Body>
                                <Accordion.Body>
                                    <FontAwesomeIcon icon={faX} className="text-danger"></FontAwesomeIcon>
                                    <input className="awap" type="text" id={"a" + (this.idAnswer + 2)} placeholder="Réponse 3"></input>
                                    <Dropzone />
                                </Accordion.Body>
                                <Accordion.Body>
                                    <FontAwesomeIcon icon={faX} className="text-danger"></FontAwesomeIcon>
                                    <input className="awap" type="text" id={"a" + (this.idAnswer + 3)} placeholder="Réponse 4"></input>
                                    <Dropzone />
                                </Accordion.Body>
                            </Accordion.Item>
                    </Accordion>
            </div>
        ); 
        
        this.setState({questions : arr});
        this.idQuestions++;
        this.idAnswer += 4;
    }

    showTxt(){
        let txt = "Phrases:\n";
        let indAns = 0;

        for(let i = 0; i < this.idPhrases; i++){
            txt+= '\t'+ document.getElementById("p" + i).value + '\n';
        }

        txt += "Questions:\n"

        for(let i = 0; i < this.idQuestions; i++){
            txt+= '\t'+ document.getElementById("q" + i).value + ":\n";
            for(let j = 0; j < 4; j++){
                txt+= '\t\t'+ document.getElementById("a" + (j + indAns)).value + "\n";
            }
            indAns += 4;
        }
        alert(txt);
    }

    render(){
        let ret;
        
        if(true){//add prop isadmin
            ret =
                <>
                    <NavBar/><br/>
                    <Accordion defaultActiveKey="0">
                        <Accordion.Item eventKey="0">
                            <Accordion.Header>Phrases</Accordion.Header>
                            <Accordion.Body>{this.state.phrases}</Accordion.Body>
                            <Accordion.Body><Button className='btn-light' onClick={this.addPhrases.bind(this)}>add phrase</Button>
                            <Dropzone />
                            </Accordion.Body>
                            
                        </Accordion.Item>
                        <Accordion.Item eventKey="1">
                            <Accordion.Header>Questions et réponse</Accordion.Header>
                            <Accordion.Body>{this.state.questions}</Accordion.Body>
                            <Accordion.Body><Button className='btn-light' onClick={this.addQuestions.bind(this)}>add question</Button>
                            
                            </Accordion.Body>
                            
                        </Accordion.Item>
                    </Accordion>
                    <br/><Button className="btn-success" onClick={this.showTxt.bind(this)}>show text</Button>
                </>
        }   
        return(ret)
    }
}

export default AdminEdit;
