import logo from './logo.svg';
import './App.css';
import Login from './components/Login';
import {Selection} from './components/Selection';
import { Text } from './components/Text';
import {BrowserRouter, Routes, Route} from 'react-router-dom';
import CreationCompte from './components/CreationCompte';

function App() {
  let titres = require('./texte/Titres.json');
  const titresTxt = [];
  
  titres.forEach(data =>{
    titresTxt.push(
      <Route exact path={"/" + factor(data.Titre)} element={<Text page={factor(data.Titre)} titre={data.Titre}/>} />
    )}
  );

  return (
    <div className="container">
      <BrowserRouter>
        <Routes>
          <Route exact path="/" element={<Login />} />
          <Route exact path="/Inscription" element={<CreationCompte/>}/>
          <Route exact path="/Selection" element={<Selection isAdmin="true"/>} />
          {titresTxt}
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export function factor(t) {
  t = t.normalize("NFD").replace(/[\u0300-\u036f]/g, "")
  while(t.includes(" ")){
      t = t.replace(" ", '');
  }
  return t;
}

export default App;
