// Set state on account type
const TaskReducer = (state = {}, action) => {
  switch (action.type) {
    case "USER_ACCOUNT":
      return {...state, value: false};
    case "ADMIN_ACCOUNT":
      return {...state, value: true};
    default:
      return {...state, value: ""};
  }
};

export default TaskReducer;
