import React from "react";

class AudioTest extends React.Component {

  constructor(props) {
    super();
    this.state = {
      path: 'classAnimal',
      name: 'Pourse',
    };
  }

  playAudio = () => {

    let p = this.props.path.normalize("NFD").replace(/[\u0300-\u036f]/g, "")
    let n = this.props.name.normalize("NFD").replace(/[\u0300-\u036f]/g, "")
    const sound = require(`./../audio/${p}/${n}.mp3`);
    new Audio(sound).play();
  }

  onClick() {
    this.playAudio()
  }

  render() {
    return (
      <div>
        <button id={this.state.path + "/" + this.state.name} onClick={this.onClick.bind(this)}>PLAY AUDIO</button>
      </div>
    );
  }
}

export default AudioTest;
