import React, {Component} from 'react';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye } from '@fortawesome/free-solid-svg-icons';
import {faEyeSlash} from '@fortawesome/free-solid-svg-icons';
import {faArrowRight} from '@fortawesome/free-solid-svg-icons';
import { unmountComponentAtNode, render } from "react-dom";

export class Login extends Component {
    constructor(){
        super();
        this.state = {btnText : "Afficher mot de passe", inputType : "password", icon : faEyeSlash};
        this.show = false;
        this.valueUSRN = "";
        this.valuePWD = "";
    }
    
    toggleShow(){
        this.show = !this.show;
        this.setState({btnText : this.show ? "Cacher mot de passe" : "Afficher mot de passe", 
                        inputType : this.show ? "text" : "password", 
                        icon : this.show ? faEye : faEyeSlash});
    }

    setValue(val, id){
        if(id == "usrn")
            this.valueUSRN = val;
        else
            this.valuePWD = val;
    }

    connexion(){
        if(this.valueUSRN == "user" && this.valuePWD == ""){
            var x = document.getElementById("root");
 
            x.style.display = "none";
            
        }
    }

    render(){
        return (
            <Container className="p-3">
                <Container className="p-5 mb-4 bg-dark rounded-3 text-white vertical-center">
                    <center className="">
                        <h1 className="header">Yeet and Delete</h1>
                        <input type="text" id="usrn" placeholder="Nom d'utilisateur" onChange={(e) => this.setValue(e.target.value, e.target.id)}></input>
                        <br></br><br></br>
                        <input type={this.state.inputType} id="pwd" name="pwd" placeholder="Mot de passe" size="15" onChange={(e) => this.setValue(e.target.value, e.target.id)}></input>
                        <Button className="btn-secondary" onClick={this.toggleShow.bind(this)}>
                            <FontAwesomeIcon icon={this.state.icon}></FontAwesomeIcon>
                        </Button>
                        <br></br>
                        <Button className="btn-secondary btn-lg" onClick={this.connexion.bind(this)}>
                            <FontAwesomeIcon icon={faArrowRight}></FontAwesomeIcon>
                        </Button>
                    </center>
                </Container>
            </Container>
        );
    }
}
