import lienData from './../images/3elien.json';
import Resizer from "react-image-file-resizer";


const images = getImagePaths(require.context('./../images/3elien', false, /\.(png|jpe?g|svg)$/));

class App extends Component {
    constructor(props) {
      super(props);
      this.fileChangedHandler = this.fileChangedHandler.bind(this);
      this.state = {
        newImage: "",
      };
    }
  
    fileChangedHandler(event) {
      var fileInput = false;
      if (event.target.files[0]) {
        fileInput = true;
      }
      if (fileInput) {
        try {
          Resizer.imageFileResizer(
            event.target.files[0],
            300,
            300,
            "JPEG",
            100,
            0,
            (uri) => {
              console.log(uri);
              this.setState({ newImage: uri });
            },
            "base64",
            200,
            200
          );
        } catch (err) {
          console.log(err);
        }
      }
    }
}
export function ImageWord() {
    return (
        <div>
            {
                lienData.map((data, index) => (
                    <div key={index}>
                        <br />
                        {
                            data.imageListe.map((imageName, indexImage) => (
                                <div key={indexImage}>
                                    
                                    {imageName == images ? (
                                        null
                                    ) : (
                                        
                                        <img   src={images[imageName + '.png']} /> 
                                    )
                                    }
                                </div>
                            ))
                        }
                        <br />
                        {data.phrase}
                    </div>
                ))
            }
        </div>
    );
}

function getImagePaths(i) {

    let images = {};
    i.keys().map((item) => { images[item.replace('./', '')] = i(item); });
    return images;
}
