import React from "react";
import sound from './../audio/3elien/sentence_1.mp3'

class AudioTest extends React.Component{

    constructor(props) {
        super();
        this.path = props;
    }

  playAudio = () => {
    const sound = require(`./../audio/${this.path.path}/${this.path.name}.mp3`);
    new Audio(sound).play();
  }

  render() {
    return (
        <div>
          <button onClick={this.playAudio}>PLAY AUDIO</button>
        </div>
    );
  }
}

export default AudioTest;
