export const TasksReducer = (tasks = [], action) => {
  switch (action.type) {
    case "GET_TASKS":
      return action.payload;
    case "ADD_TASK":
      return tasks;
    case "REMOVE_TASK":
      return tasks;
    default:
      return tasks;
  }
};

export default TasksReducer;
