const birthdayArray = [
    { name: 'John', birthday: '25/12/1990', id: '123' },
    { name: 'Abby', birthday: '22/10/1991', id: '124' },
];

const images = loadAllImage(require.context('./../images/3elien', false, /\.(png|jpe?g|svg)$/));

export function ImageWord() {
    return (
        <div>
            {birthdayArray.map(({ name, birthday, id }) => (
                <p key={id}> {name}'s birthday is on {birthday} </p>
            ))}
            
        </div>
        
    );
}

function loadAllImage(i) {
    
    let images = {};
    i.keys().map((item, index) => { images[item.replace('./','')] = i(item); });

    return images;
}

function loadtexte(t) {
    
    let texte = {};
        if (window.File && window.FileReader && window.FileList && window.Blob) {
            var preview = document.getElementById('show-text');
            var file = document.querySelector('input[type=file]').files[0];
            var reader = new FileReader()

            var textFile = /text.*/;

            if (file.type.match(textFile)) {
            reader.onload = function (event) {
                preview.innerHTML = event.target.result;
            }
            } else {
            preview.innerHTML = "<span class='error'>It doesn't seem to be a text file!</span>";
            }
            reader.readAsText(file);

    } else {
        alert("Your browser is too old to support HTML5 File API");
    }
    return texte;
}
