export function ImageQuiz(date, p) {
    const images = getImagePaths(require.context('./../images/', false, /\.(png|jpe?g|svg)$/));

    let choix =
        <div id={p}>
            {p}
            {
                date.reponse.map((choix, indexRep) => (
                    
                    <div className="check" id={indexRep}>
                        <input type="checkbox" value={indexRep} />{choix.phrase}
                        {
                            data.imageListe.map((imageName, indexImage) => (
                                <u key={indexImage}>
                                    {imageName == images ? (
                                        null
                                    ) : (
                                        <img id="imageWord" src={images[imageName + '.png']} />
                                    )
                                    }
                                </u>
                            ))

                        } 
                    </div>
                ))
            }
        </div>
    return (choix);
}
