import React, {Component} from 'react';
import './App.css';
import Login from './components/Login';
import {Selection} from './components/Selection';
import { Text } from './components/Text';
import {BrowserRouter, Routes, Route} from 'react-router-dom';
import CreationCompte from './components/CreationCompte';
import AdminEdit from './components/AdminEdit';
import SettingsCompte from './components/SettingsCompte';

class App extends Component{
  
  constructor(props){
    super();
    //for the text pages
    this.titres = require('./texte/Titres.json');
    this.titresTxt = [];
    
    //to take care of the admin status
    this.state = {isAdmin : false};
    this.isAdminTrue = this.isAdminTrue.bind(this);
    this.isAdminFalse = this.isAdminFalse.bind(this);

    //generate all the elements for the dropdown selector
    this.genLinks = this.genLinks.bind(this);
  }
  
  factor(t) {
    t = t.normalize("NFD").replace(/[\u0300-\u036f]/g, "")
    while(t.includes(" ")){
        t = t.replace(" ", '');
    }
    return t;
  }

  isAdminTrue(){
    console.log(this.state.isAdmin);
    this.setState({isAdmin : true});
    console.log("true:" + this.state.isAdmin);
  }

  isAdminFalse(){
    console.log(this.state.isAdmin);
    this.setState({isAdmin : false});
    console.log("false:" + this.state.isAdmin);
  }

  genLinks(){
    this.titresTxt = [];
    this.titres.forEach(data =>{
      this.titresTxt.push(
        <Route exact path={"/" + this.factor(data.Titre)} element={<Text page={this.factor(data.Titre)} isAdmin={this.state.isAdmin} titre={data.Titre}/>} />
      )}
    );
  }

  render(){
    return (
      <div className="container">
        <BrowserRouter>
          <Routes>
            <Route exact path="/" element={<Login callbackAdminTrue={this.isAdminTrue} callbackAdminFalse={this.isAdminFalse} callbackGenLinks={this.genLinks}/>} />
            <Route exact path="/Inscription" element={<CreationCompte/>}/>
            <Route exact path="/Selection" element={<Selection isAdmin={this.state.isAdmin}/>} />
            <Route exact path="/SettingsCompte" element={<SettingsCompte isAdmin={this.state.isAdmin}/>} />
            <Route exact path="/Edit" element={<AdminEdit isAdmin={this.state.isAdmin}/>} />
            {this.titresTxt}
          </Routes>
        </BrowserRouter>
      </div>
    );
  }
}



export default App;
