const images = loadAllImage(require.context('./../images/3elien', false, /\.(png|jpe?g|svg)$/));

export function ImageWord() {
    if (isLoggedIn) {
        button = <LogoutButton onClick={this.handleLogoutClick} />;
      } else {
        button = <LoginButton onClick={this.handleLoginClick} />;
      }
    return (
        <div>
            {
                lienData.map((data) => (
                    <div>
                        <br />
                        {
                            data.imageListe.map((imageName) => (
                                <div>
                                    displayImage({imageName})
                                </div>
                            ))
                        }
                        <br />
                        {data.phrase}
                    </div>
                ))
            }
        </div>

    );
}

function loadAllImage(i) {

    let images = {};
    i.keys().map((item) => { images[item.replace('./', '')] = i(item); });

    return images;
}

