import { getImagePaths } from './ImageWord';

export function ImageQuiz(date, p) {
    const images = getImagePaths(require.context('./../images/', false, /\.(png|jpe?g|svg)$/));

    let choix =
        <div id={p}>
            {p}
            {
                date.reponse.map((choix, indexRep) => (
                    <u className="check" id={choix}>
                        <input type="checkbox" value={choix} />{choix}
                    </u>
                ))
            }
        </div>
    return (choix);
}
