import lienData from './../images/3elien.json';


let imagePaths  = getImagePaths(require.context('./../images/3elien', false, /\.(png|jpe?g|svg)$/));

let images = [];
imagePaths.map((path) => images.push(require("../" + path)));


export function ImageWord() {
    return (
        <div>
            {
                lienData.map((data, index) => (
                    <div key={index}>
                        
                        <br />
                        {
                            data.imageListe.map((imageName, indexImage) => (
                                <div key={indexImage}>
                                    
                                    {imageName == images ? (
                                        null
                                    ) : (
                                        <div >
                                            <img key={indexImage} src={imageName} alt={imageName} />
                                        </div>
                                    )
                                    }
                                </div>
                            ))
                        }
                        <br />
                        {data.phrase}
                    </div>
                ))
            }
        </div>
    );
}

function getImagePaths(i) {

    let images = {};
    //i.keys().map((item, index) => { images[item.replace('./', '')] = i(item); });
    return images;
}
