import { ImageQuiz } from './ImageQuiz';
import AudioTest from './Audio.js';

export function ImageWord(path, isQuiz) {

    let lienData = require('./../texte/' + path + '.json');
    const shuffle = arr => [...arr].sort(() => Math.random() - 0.5);

    let images = getImagePaths(require.context(("./../images/"), false, /\.(png|jpe?g|svg)$/));
    let name = 'sentence_0';
    let ret = lienData.map((data, index) => (
        <div key={index} id={index}>
            {console.log(index)}
            <br />
            {
                data.imageListe.map((imageName, indexImage) => (
                    <u key={indexImage}>
                        {imageName == images ? (
                            null
                        ) : (
                            <img id="imageWord" src={images[imageName + '.png']} />
                        )
                        }
                    </u>
                ))

            }
            <br />
            {
                isQuiz ?
                    (
                        ImageQuiz(data, data.phrase)

                    ) : (
                        data.phrase
                    )
            }
            {
                name.toString().replace(index.toString(), (index+1).toString())
            }

            <AudioTest path={path} name={name} />

        </div>
    ))


    if (isQuiz)
        return (
            shuffle(ret));
    else
        return (ret);

}


export function getImagePaths(i) {
    let images = {};
    i.keys().map((item) => { images[item.replace('./', '')] = i(item); });
    return images;
}
