import lienData from './../images/3elien.json';
import Resizer from "react-image-file-resizer";


const images = getImagePaths(require.context('./../images/3elien', false, /\.(png|jpe?g|svg)$/));





export function ImageWord() {
    return (
        <div>
            {
                lienData.map((data, index) => (
                    <div key={index}>
                        <br />
                        {
                            data.imageListe.map((imageName, indexImage) => (
                                <h1  key={indexImage}>
                                    
                                    {imageName == images ? (
                                        null
                                    ) : (
                                        
                                        <img id="imageWord" src={images[imageName + '.png']} /> 
                                    )
                                    }
                                </h1>
                            ))
                        }
                        <br />
                        {data.phrase}
                    </div>
                ))
            }
        </div>
    );
}

function getImagePaths(i) {

    let images = {};
    i.keys().map((item) => { images[item.replace('./', '')] = i(item); });
    return images;
}
