import { connect } from "react-redux";

import { addTask } from "../actions/TasksActions";

export const AddTask = (props) => {
  return (
    
    <div>
      
      <button onClick={() => props.add(_title.value)}>Ajouter</button>
    </div>
  );
};

export default connect(null, { add: addTask })(AddTask);
