import React from "react";

import { connect } from "react-redux";

import { addTask } from "../actions/TasksActions";



//bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
//images
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';


store.subscribe(() => {
  let state  = [] 
  state = store.getState()
  console.log(state.tasks.value)
  if (state.tasks.value) {
      Navigation.navigate('/Selection');
  }
  else if (!state.tasks.value) {
      Navigation.navigate('/Selection');
  } else
      alert("Mauvais nom d'utilisateur ou mot de passe. \nVeuillez réessayer.");
})
const AddTask = (props) => {
  return (
      <Button className="btn-secondary btn-xl mt-2" onClick={() => props.add(props.data)} id="submit">
        <FontAwesomeIcon icon={faArrowRight} size="lg" />
      </Button>
  );
};

export default connect(null, { add: addTask })(AddTask);
