    return (
        <div>
            {
                lienData.map((data, index) => (
                    <div key={index}>
                        
                        <br />
                        {
                            data.imageListe.map((imageName, indexImage) => (
                                <div key={indexImage}>
                                    
                                    {imageName == images ? (
                                        null
                                    ) : (
                                        <div >
                                            <img key={index} src={img} alt={img} />
                                        </div>
                                    )
                                    }
                                </div>
                            ))
                        }
                        <br />
                        {data.phrase}
                    </div>
                ))
            }
        </div>
    );
}

function loadAllImage(i) {

    let images = {};
    i.keys().map((item) => { images[item.replace('./', '')] = i(item); });

    return images;
}
