const images = getImagePaths(require.context('./../images/', false, /\.(png|jpe?g|svg)$/));

export function ImageWord(path, isQuiz) {
    let lienData = require('./../' + path + '.json');
        console.log(lienData);
    return (
        <div>
            {
                lienData.map((data, index) => (
                    <div key={index}>
                        <br />
                        {
                            data.imageListe.map((imageName, indexImage) => (
                                <u key={indexImage}>

                                    {imageName == images ? (
                                        null
                                    ) : (
                                        <img id="imageWord" src={images[imageName + '.png']} />
                                    )
                                    }
                                </u>
                            ))
                        }
                        <br />
                        {data.phrase}
                    </div>
                ))
            }
        </div>
    );
}

function getImagePaths(i) {
    let images = {};
    i.keys().map((item) => { images[item.replace('./', '')] = i(item); });
    return images;
}
