import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Button from 'react-bootstrap/Button';
import { faCheck } from '@fortawesome/free-solid-svg-icons';
import UserInfo from './UserInfo.js';

class QuizValide extends React.Component {

  constructor(props) {
    super();
    this.state = {
      path: 'classAnimal',
      name: 'Pourse',
    };
  }

  validateQuiz = () => {
    let boxGood = document.getElementsByClassName(true);
    let boxBad = document.getElementsByClassName(false);

    let score = 0;

    for (const element of boxGood) {
      if (element.checked) {
        score++;
      } else {
        score--;
      }
    }
    for (const element of boxBad) {
      if (element.checked) {
        score--;
      }
    }
    this.UserInfo.UpdateScoreTexte(score);
  }

  onClick() {
    this.validateQuiz()
  }

  render() {
    return (
      <div>
        <Button className="btn-secondary" onClick={this.onClick.bind(this)}>
          <FontAwesomeIcon icon={faCheck} id="Quiz" ></FontAwesomeIcon>
        </Button>
      </div>
    );
  }
}

export default QuizValide;
