import React from "react";
import sound from './../audio/3elien/sentence_01.mp3'

class AudioTest extends React.Component{

    constructor(props) {
        super(props);
        this.path = "";
        this.name = "";
    }

  playAudio = () => {
    ///new Audio("audio/" + this.path + "/" + this.name + ".mp3").play();
    const sound = require(`./../audio/${this.path}/${ this.name}.mp3`);
    new Audio(sound).play();
  }

  render() {
    return (
        <div>
          <button onClick={this.playAudio}>PLAY AUDIO</button>
        </div>
    );
  }
}

export default AudioTest;
