import React, {Component} from 'react';
import Nav from 'react-bootstrap/Nav';
import { NavLink } from 'react-router-dom';
import Dropdown from 'react-bootstrap/Dropdown';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faFileLines } from '@fortawesome/free-solid-svg-icons';
import { faArrowUpFromBracket } from '@fortawesome/free-solid-svg-icons';
import { faGear } from '@fortawesome/free-solid-svg-icons';
//custom component
import BtnDropdown from './BtnDropdown';
import store from '../store/index.js'

export default function NavBar(isAdmin) {

    let titres = require('./../texte/Titres.json');
    const titresTxt = [];
    const btnEdit = [];

    titres.forEach(data =>{
        titresTxt.push(
            <Dropdown.Item>
                <Nav.Link as={NavLink} exact to={"/" + factor(data.Titre)} replace><b className='text-black'>{data.Titre}</b></Nav.Link>
                {/* <BtnDropdown path={"/" + factor(data.Titre)} name={data.Titre}></BtnDropdown> */}
                
            </Dropdown.Item>
        )
    });
    let state = store.getState()


    store.subscribe(() => {
        
        console.log(state)
        if (state.tasks.value == "admin") {
             this.props.navigate('/Selection');
        }
        else if (state.tasks.value == "user") {
             this.props.navigate('/Selection');
        } else
            error = "Mauvais nom d'utilisateur ou mot de passe. \nVeuillez réessayer.";
    })

    if(isAdmin.isAdmin == true){
        btnEdit.push(<Nav.Link as={NavLink} exact to="/Edit" replace><div className='text-danger text-center'><u>edit</u></div></Nav.Link>);
    }

    return(
        <Nav className="me-auto bg-light border rounded-0" expand="lg">
            <Dropdown>
                <Dropdown.Toggle variant="info" id="dropdown-basic" className='border rounded-0'>
                    *NOM DU COMPTE*
                </Dropdown.Toggle>

                <Dropdown.Menu>
                    <Dropdown.Item>
                        <Nav.Link className="border" as={NavLink} exact to="/SettingsCompte" replace>
                            <FontAwesomeIcon className='text-black' icon={faGear}></FontAwesomeIcon><b className='text-black'> Settings</b>
                        </Nav.Link>
                    </Dropdown.Item>
                    <Dropdown.Item>
                        <Nav.Link className="border" as={NavLink} exact to="/" replace>
                            <FontAwesomeIcon className='text-black' icon={faArrowUpFromBracket} style={{transform: 'rotate(270deg)' }}></FontAwesomeIcon><b className='text-black'> Déconnexion</b>
                        </Nav.Link>
                    </Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
            <Dropdown>
                <Dropdown.Toggle variant="info" id="dropdown-basic" className='border rounded-0'>
                    <FontAwesomeIcon icon={faFileLines}></FontAwesomeIcon>
                    <b> Textes</b>
                </Dropdown.Toggle>

                <Dropdown.Menu>
                    {titresTxt}
                </Dropdown.Menu>
            </Dropdown>
            {btnEdit}
        </Nav>
    )
}


export function factor(t) {
    t = t.normalize("NFD").replace(/[\u0300-\u036f]/g, "");
    while(t.includes(" ")){
        t = t.replace(" ", '');
    }
    return t;
}
