}

export function getAudioName(name, index) {
    name = name.replace(index.toString(), (index + 1).toString())
    return name;
}

export function getImagePaths(i) {
    let images = {};
    i.keys().map((item) => { images[item.replace('./', '')] = i(item); });
    return images;
}
